jQuery(function($){
	$("#themify-pageoptions-popup").css('height', 500);
	$("#themify-pageoptions-popup").css('width', 600);
	$(".themify_pageoptions_popup").click(function() {
		$.magnificPopup.open({
			items: {
				src: ( '#themify-pageoptions-popup' ),
				type: 'inline',
				overflowY: 'scroll',
				closeBtnInside: true
			},
			callbacks : {
				open : function(){
					$( 'body' ).addClass( 'showing-page-options' );
				},
				close : function(){
					$( 'body' ).removeClass( 'showing-page-options' );
				}
			}
		});
	});

	$('#pageoptions_submit_settings').click(function(e){
		$( ".alert" ).addClass( "busy" ).fadeIn(800);
		$.ajax({
			data: $("#ajax-pageoptions-form").serialize(),
			type: 'post',
			url: tfPageOptionsVars.ajaxurl,
			success: function( data ) {
				$.magnificPopup.close();
				console.log(data);
			}
		});
	});
});